import React from 'react';
import Link from 'next/link';
import useAxios from 'axios-hooks';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios('http://localhost:3100/api/shop');
  console.log('data:::::', data);

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product) => (
        <Link href="/product/[slug]" as={`/product/${product.id}`}>
          <p>{product.name}</p>
          <p>{product.price}</p>
        </Link>
      ))}
    </div>
  );
};

export default ShopList;
