import React, { useState } from 'react';
import Link from 'next/link';
import Item from '../item';

const ProductRow = (product, onCardItemChange, onItemAddition) => {
  //   const;

  return (
    <div key={product._id}>
      <Item product={product} />
      <Link href="/shop/product/[id]" as={`/shop/product/${product._id}`}>
        <a>view product</a>
      </Link>
      <label id="quantity-select">Select a quantity:</label>
      <select
        name="item-quantity"
        id="quantity-select"
        value={product.inStockQuantity}
        onChange={(e) => setItemQuantity(e.target.value)}
      >
        {[...Array(product.inStockQuantity)].map((u, i) => (
          <option key={i} value={i}>
            {i}
          </option>
        ))}
      </select>
      <button type="submit" onClick={(e) => addItemAndQuantity(e.relatedTarget)}>
        Add
      </button>
    </div>
  );
};

export default ProductRow;
