import React, { useState } from 'react';
import config from '../../config';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import Item from '../../components/item';
import { Product } from '../../interfaces';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios(config.getProductList);

  const [{ data: putData, loading: putLoading, error: putError }, executePut] = useAxios(
    {
      url: config.addItem,
      method: 'POST',
    },
    { manual: true }
  );
  const [itemQuantity, setItemQuantity] = useState(data);

  const saveItemNumber = (number) => {
    console.log;
    setItemQuantity(number);
  };
  const submitQuantity = () => {
    console.log('submitting: ');
    return executePut({ data: { itemQuantity, updatedAt: new Date().toISOString() } });
  };

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <div key={product._id}>
          <Item product={product} />
          <Link href="/shop/product/[id]" as={`/shop/product/${product._id}`}>
            View
          </Link>
          <button type="submit" onClick={submitQuantity}>
            Add
          </button>
          {/* <label for="pet-select">Select a quantity:</label> */}
          <select name="pets" id="pet-select">
            {[...Array(product.inStockQuantity)].map((u, i) => (
              <option value={i}>i</option>
            ))}
          </select>
          {product.inStockQuantity}
          <input
            type="number"
            min="1"
            max={product.inStockQuantity}
            placeholder="1"
            onChange={(e) => saveItemNumber(e.target.value)}
          />
        </div>
      ))}
    </div>
  );
};

export default ShopList;
