import React, { useState } from 'react';
import config from '../../config';
import useAxios from 'axios-hooks';
import Item from '../item';

const ProductRow = ({ product, savedQuantity }) => {
  const [selectedQuantity, setSelectedQuantity] = useState(savedQuantity);
  const [itemAdded, setItemAdded] = useState(true);
  // make check to to add item before reload.
  const [{ data: postData, loading: putLoading, error: putError }, executePost] = useAxios(
    {
      url: config.addItem,
      method: 'POST',
    },
    { manual: true }
  );

  const onQuantityUpdate = (value) => {
    setSelectedQuantity(value);
    setItemAdded(false);
  };

  const onItemUpdate = () => {
    const postData = {
      ...product,
      quantity: selectedQuantity,
    };
    executePost({ data: postData });
    setItemAdded(true);
  };

  if (!itemAdded) {
    alert('You have not added to your basket all the items, add them to not loose the information.');
  }

  if (postData && postData.message && postData.message === 'error') {
    alert('we do not have that much stock, please adjust quantity');
  }

  return (
    <div key={product._id}>
      <Item product={product} />
      <p>In stock {product.quantity}</p>
      <label id="quantity-select">Select a quantity:</label>
      <select
        name="item-quantity"
        id="quantity-select"
        value={selectedQuantity}
        onChange={(e) => onQuantityUpdate(parseInt(e.target.value))}
      >
        {[...Array(product.inStockQuantity)].map((u, i) => (
          <option key={i} value={i + 1}>
            {i + 1}
          </option>
        ))}
      </select>
      <button type="submit" onClick={onItemUpdate}>
        Add
      </button>
    </div>
  );
};

export default ProductRow;
