import React from 'react';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import ItemView from './item/[slug]';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios('http://localhost:3100/api/shop');

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product) => {
        console.log('product', product._id);
        return (
          <div key={product._id}>
            <ItemView product={product} />
            <Link href="/shop/product/[slug]" as={`/shop/product/${product._id}`}>
              <p>Add</p>
            </Link>
          </div>
        );
      })}
    </div>
  );
};

type Product = {
  _id: number;
  name: string;
  quantity: number;
  price: number;
};
export default ShopList;
