import React, { useState } from 'react';
import config from '../../config';
import Link from 'next/link';
import useAxios from 'axios-hooks';
// import Item from '../../components/item';
import { Product } from '../../interfaces';
import ProductRow from '../../components/product-row';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios(config.getProductList);

  // create object with prices and ids
  const [cartItems, setCartItems] = useState({});

  // const onFormSubmit = () => {
  //   onSubmit(formData);
  //   return false;
  // };
  const updateCartItemInformation = (itemKey) => (value) => {
    let basket = { ...cartItems };
    console.log('item to update:', itemKey, value);
    // if basket[fieldKey] = value; then add or update the value and the
    setFormData(data);
  };

  const onItemAddition = (item) => {
    console.log('here, ', item);
  };

  // const [{ data: putData, loading: putLoading, error: putError }, executePut] = useAxios(
  //   {
  //     url: config.addItem,
  //     method: 'POST',
  //   },
  //   { manual: true }
  // );

  // <UserDropdown
  //           title={translate('form.field.occupation.title')}
  //           placeholder={translate('components.select.minimalist_placeholder')}
  //           fieldValue={formData[FORM_FIELD_DATA.OCCUPATION]}
  //           onFormDataChange={setFormDataValue(FORM_FIELD_DATA.OCCUPATION)}
  //           keys={USER_OCCUPATION}
  //         />
  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <ProductRow
          key={product._id}
          product={product}
          onCardItemChange={updateCartItemInformation()}
          onItemAddition={onItemAddition()}
        />
      ))}
    </div>
  );
};

export default ShopList;
