import React, { useState } from 'react';
import Link from 'next/link';
import Item from '../item';

const ProductRow = (product, onCardItemChange, onItemAddition) => {
  //   const;
  console.log('product in row: ', product);

  const [{ selectedQuantity, setSelectedQuantity }] = useState(0);

  return (
    <div key={product._id}>
      <Item product={product} />
      <label id="quantity-select">Select a quantity:</label>
      <select
        name="item-quantity"
        id="quantity-select"
        value={selectedQuantity}
        onChange={(e) => onCardItemChange(e.target.value)}
      >
        {[...Array(product.inStockQuantity)].map((u, i) => (
          <option key={i} value={i}>
            {i}
          </option>
        ))}
      </select>
      <button type="submit" onClick={(e) => onItemAddition(e.relatedTarget)}>
        Add
      </button>
    </div>
  );
};

export default ProductRow;
