import React from 'react';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import ItemView from './item/[slug]';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios('http://localhost:3100/api/shop');

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  if (!loading && !error) {
    data &&
      data.map((product) => {
        console.log(product.id);
        return product.id;
      });
  }
  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <Link key={parseInt(product._id)} href="/shop/item/[slug]" as={`/shop/item/${product.id}`}>
          <ItemView product={product} />
        </Link>
      ))}
    </div>
  );
};

type Product = {
  _id: String;
  name: String;
  quantity: Number;
  price: Number;
};
export default ShopList;
