import React, { useState } from 'react';
import config from '../../config';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import Item from '../../components/item';
import { Product } from '../../interfaces';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios(config.getProductList);
  const [{ data: putData, loading: putLoading, error: putError }, executePut] = useAxios(
    {
      url: config.addItem,
      method: 'PUT',
    },
    { manual: true }
  );
  const [quantity, setQuantity] = useState(1);

  const saveItemNumber = (number) => setQuantity(number);
  const submitQuantity = () => executePut({ data: { quantity, updatedAt: new Date().toISOString() } });

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <div key={product._id}>
          <Item product={product} />
          <Link href="/shop/product/[id]" as={`/shop/product/${product._id}`}>
            View
          </Link>
          <button type="submit" onClick={submitQuantity}>
            Add
          </button>
          <input type="number" min="1" placeholder="1" onChange={(e) => saveItemNumber(e.target.value)} />
        </div>
      ))}
    </div>
  );
};

export default ShopList;
