import React, { useState, useEffect } from 'react';
import config from '../../../config';
import { Product } from '../../../interfaces';
import useAxios from 'axios-hooks';

const Checkout = () => {
  const [{ data, loading, error }, refetch] = useAxios(config.getProductList);
  const [{ data: cartData, loading: loadingCart, error: cartError }, refetchCart] = useAxios(config.getShoppingCart);

  if (loadingCart) {
    return <p>Loading</p>;
  }

  if (cartError) {
    return <p>There's been an issue with this cart.</p>;
  }

  const getItemInCart = (productId: number) => cartData.cartItems.find((item) => item._id === productId);
  const getCurrentQuantity = (product: Product) => (getItemInCart(product._id) ? getItemInCart(product._id).quantity : 0);

  console.log('router:', cartData);
  return (
    <>
      <p>Your cart here:</p>
      <p>Total items: {cartData.totalItems}</p>
      <p>Total price: {cartData.totalPrice} Euros</p>
      {cartData.cartItems.map((item) => (
        <div key={item._id}>
          <p>Item name: {item.name}</p>
          <p>Total item/s price: {item.price}</p>
          <p>Total quantity:</p>
          {/* <select
            name="item-quantity"
            id="quantity-select"
            value={selectedQuantity}
            onChange={(e) => onQuantityUpdate(parseInt(e.target.value))}
          >
            {[...Array(product.inStockQuantity)].map((u, i) => (
              <option key={i} value={i + 1}>
                {i + 1}
              </option>
            ))}
          </select> */}
          <button type="submit">Remove item from basket</button>
        </div>
      ))}
    </>
  );
};

export default Checkout;
