import React, { useState } from 'react';
import config from '../../config';
import useAxios from 'axios-hooks';
import Link from 'next/link';
import Item from '../item';

const ProductRow = ({ product, savedQuantity }) => {
  const [selectedQuantity, setSelectedQuantity] = useState(savedQuantity);
  console.log('selected quantity', selectedQuantity);
  // find the  users
  const [{ data: putData, loading: putLoading, error: putError }, executePost] = useAxios(
    {
      url: config.addItem,
      method: 'POST',
    },
    { manual: true }
  );
  // show and check whether there is a selected quantity otherwise, show one.
  const onItemAddition = () => {
    const putData = {
      ...product,
      quantity: selectedQuantity,
    };
    console.log('product', putData);
    executePost({ data: putData });
  };

  return (
    <div key={product._id}>
      <Item product={product} />
      <label id="quantity-select">Select a quantity:</label>
      <select
        name="item-quantity"
        id="quantity-select"
        value={selectedQuantity}
        onChange={(e) => setSelectedQuantity(parseInt(e.target.value))}
      >
        {[...Array(product.inStockQuantity)].map((u, i) => (
          <option key={i} value={i + 1}>
            {i + 1}
          </option>
        ))}
      </select>
      <button type="submit" onClick={onItemAddition}>
        Add
      </button>
    </div>
  );
};

export default ProductRow;
