import React, { useState } from 'react';
import config from '../../config';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import Item from '../../components/item';
import { Product } from '../../interfaces';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios(config.getProductList);
  // const [{ data: putData, loading: putLoading, error: putError }, executePut] = useAxios(
  //   {
  //     url: config.addItem,
  //     method: 'POST',
  //   },
  //   { manual: true }
  // );

  const [updateCart, setUpdateCart] = useState(data);
  const [itemQuantity, setItemQuantity] = useState('1');

  const addItemAndQuantity = (product) => {
    console.log('selected product', product);
    console.log('cart: ', updateCart);
    // setUpdateCart({ product });
  };
  // const addItemAndQuantity = (product) => {
  //   console.log('submitting: ');
  //   // return executePut({ data: { cartQuantity, updatedAt: new Date().toISOString() } });
  // };

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <div key={product._id}>
          <Item product={product} />
          <Link href="/shop/product/[id]" as={`/shop/product/${product._id}`}>
            <a>view product</a>
          </Link>
          <label id="quantity-select">Select a quantity:</label>
          <select
            name="item-quantity"
            id="quantity-select"
            value={itemQuantity}
            onChange={(e) => setItemQuantity(e.target.value)}
          >
            {[...Array(product.inStockQuantity)].map((u, i) => (
              <option key={i} value={i}>
                {i}
              </option>
            ))}
          </select>
          {/* <button type="submit" onClick={(e) => addItemAndQuantity(e.relatedTarget)}>
            Add
          </button> */}
        </div>
      ))}
    </div>
  );
};

export default ShopList;
