import React, { useState } from 'react';
import config from '../../config';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import { Product } from '../../interfaces';
import ProductRow from '../../components/product-row';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios(config.getProductList);

  const onItemAddition = (item) => {
    console.log('here, ', item);
  };

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <div key={product._id}>
          <ProductRow product={product} onItemAddition={onItemAddition} />
          <Link href="/shop/product/[id]" as={`/shop/product/${product._id}`}>
            <a>view product</a>
          </Link>
        </div>
      ))}
    </div>
  );
};

export default ShopList;
