import React, { useState } from 'react';
import Link from 'next/link';
import Item from '../item';

const ProductRow = ({ product, onCardItemChange, onItemAddition }) => {
  //   const;
  console.log('product in row: ', product.inStockQuantity);

  const [selectedQuantity, setSelectedQuantity] = useState('1');

  return (
    <div key={product._id}>
      <Item product={product} />
      <label id="quantity-select">Select a quantity:</label>
      <select
        name="item-quantity"
        id="quantity-select"
        value={selectedQuantity}
        onChange={(e) => setSelectedQuantity(e.target.value)}
      >
        {[...Array(product.inStockQuantity)].map((u, i) => {
          console.log('what unit here: ', u, i);
          return (
            <option key={i} value={i + 1}>
              {i}
            </option>
          );
        })}
      </select>
      <button type="submit" onClick={(e) => onItemAddition(e.relatedTarget)}>
        Add
      </button>
    </div>
  );
};

export default ProductRow;
