import React, { useState } from 'react';
import config from '../../config';
import useAxios from 'axios-hooks';
import Link from 'next/link';
import Item from '../item';

const ProductRow = ({ product }) => {
  const [selectedQuantity, setSelectedQuantity] = useState(`${product.quantity}: 1`);
  console.log('selected quantity', selectedQuantity);
  // on Add, update the item in the server
  const [{ data: putData, loading: putLoading, error: putError }, executePost] = useAxios(
    {
      url: config.addItem,
      method: 'POST',
    },
    { manual: true }
  );

  const onItemAddition = () => {
    console.log('product', product);
  };

  return (
    <div key={product._id}>
      <Item product={product} />
      <label id="quantity-select">Select a quantity:</label>
      <select
        name="item-quantity"
        id="quantity-select"
        value={selectedQuantity}
        onChange={(e) => setSelectedQuantity(e.target.value)}
      >
        {[...Array(product.inStockQuantity)].map((u, i) => (
          <option key={i} value={i + 1}>
            {i + 1}
          </option>
        ))}
      </select>
      <button type="submit" onClick={onItemAddition}>
        Add
      </button>
    </div>
  );
};

export default ProductRow;
