import React, { Children } from 'react';
import Link from 'next/link';
import useAxios from 'axios-hooks';
import Item from '../../components/item';

const ShopList = () => {
  const [{ data, loading, error }, refetch] = useAxios('http://localhost:3100/api/shop');

  if (loading) {
    return <p>loading</p>;
  }

  if (error) {
    return <p>Our list broke!</p>;
  }

  return (
    <div>
      <h2>Product List</h2>
      {data.map((product: Product) => (
        <div key={product._id}>
          <Item product={product} />
          <Link href="/shop/product/[slug]" as={`/shop/product/${product._id}`}>
            {/* <p>Add</p> */}
            {Children}
          </Link>
        </div>
      ))}
    </div>
  );
};

type Product = {
  _id: number;
  name: string;
  quantity: number;
  price: number;
};
export default ShopList;
